import { ReactNode, CSSProperties } from 'react';
export type LogoProps = {
    /**
     * The brand name to display. Defaults to 'LOOKMIX'.
     */
    name?: string;
    /**
     * Optional slogan to display below the name.
     */
    slogan?: string;
    /**
     * If provided, the logo becomes a navigation link to this URL.
     */
    href?: string;
    /**
     * Size of the logo icon in pixels. Defaults to 32.
     */
    size?: number;
    /**
     * Override the default SVG logo with a custom node.
     */
    logo?: ReactNode;
    /**
     * If true, only displays the logo icon without text.
     */
    minimal?: boolean;
    /**
     * Additional class name for the container.
     */
    className?: string;
    /**
     * Inline styles for the container.
     */
    style?: CSSProperties;
};
