import { CSSProperties, MouseEventHandler } from 'react';
export type IconButtonSize = 'xs' | 'sm' | 'md' | 'lg' | 'xl';
export type IconButtonShape = 'circle' | 'squircle' | 'rounded';
export type IconButtonVariant = 'ghost' | 'filled' | 'outline' | 'surface' | 'glass';
export type IconButtonProps = {
    /**
     * Icon name (string) หรือ Icon Component (ReactNode)
     */
    icon?: string | React.ReactNode;
    href?: string;
    onClick?: MouseEventHandler<HTMLButtonElement | HTMLAnchorElement>;
    disabled?: boolean;
    size?: IconButtonSize;
    shape?: IconButtonShape;
    variant?: IconButtonVariant;
    className?: string;
    style?: CSSProperties;
    external?: boolean;
    target?: string;
    rel?: string;
    type?: 'button' | 'submit' | 'reset';
};
